jQuery('.menu_right_main_tab').off('click');window.onload=(function(oldLoad){return function(){oldLoad&&oldLoad();if(window.addEventListener){window.addEventListener('load',initApp())}else{window.attachEvent('onload',initApp())}}})(window.onload)
